import React, { Component } from 'react';

import dataServicios from '../data/servicios.json';

class Servi extends Component {
  constructor (){
    super();
    this.state ={
      dataServicios
    };
  }

  render() {

    const servic = this.state.dataServicios.map((dato,i) => {
      let tipo=(dato%2)?"order-md-2":"";
      let tipoI=(dato%2)?"order-md-1":"";
      return (
                <div>
                <h1> Services </h1>
                <div className="row featurette {tipo}">
                  <div className="col-md-7">
                    <h2 className="featurette-heading fw-normal lh-1">{dato.title}</h2>
                    <p className="lead">{dato.description}</p>
                  </div>
                  <div className="col-md-5">
                    <svg className="bd-placeholder-img bd-placeholder-img-lg featurette-image img-fluid mx-auto" width="500" height="500" xmlns="http://www.w3.org/2000/svg" role="img" aria-label="Placeholder: 500x500" preserveAspectRatio="xMidYMid slice" focusable="false"><title>Placeholder</title><rect width="100%" height="100%" fill="#eee"/><text x="50%" y="50%" fill="#aaa" dy=".3em">500x500</text></svg>
                  </div>
                  <br />
                  <hr className="featurette-divider mt-5" />
                </div>
                </div>
             )
    });
    return (    
          {servic}
     )
  }
}

export default Servi;
